mapper.select:
SELECT i.inventory_id, s.sector_name, i.product_id, p.product_name, c.category_name, su.supplier_name, p.income_unit_price, p.outgoing_unit_price, i.inventory_quantity, i.expiration_date FROM inventory i JOIN sector s ON i.sector_id = s.sector_id JOIN product p ON i.product_id = p.product_id JOIN category c ON p.category_id = c.category_id JOIN supplier su ON p.supplier_id = su.supplier_id WHERE s.warehouse_id = 1 AND p.discontinued_status = 0
AND s.sector_name LIKE CONCAT('%', #{sectorName}, '%')
AND i.expiration_date >= #{startDate}
AND i.expiration_date <= #{endDate}
AND p.product_name LIKE CONCAT('%', #{productName}, '%')
AND su.supplier_name LIKE CONCAT('%', #{supplierName}, '%')

SELECT inventory_quantity
FROM inventory
WHERE inventory_id = #{inventoryId}

mapper.delete:
DELETE FROM inventory
WHERE inventory_id IN

    #{inventoryId}
