mapper.update:
UPDATE outgoing
SET outgoing_status = #{status}
WHERE outgoing_id = #{outgoingId}

mapper.select:
SELECT o.outgoing_id AS outgoingId, o.branch_name AS branchName, p.product_name AS productName, o.outgoing_quantity AS outgoingQuantity, p.outgoing_price AS unitPrice, (o.outgoing_quantity * p.outgoing_price) AS totalPrice, o.outgoing_status AS outgoingStatus, DATE_FORMAT(o.outgoing_date, '%Y-%m-%d') AS outgoingDate FROM outgoing o JOIN product p ON o.product_id = p.product_id WHERE 1=1
AND o.outgoing_date BETWEEN #{startDate} AND #{endDate}
AND ( LOWER(p.product_name) LIKE CONCAT('%', LOWER(#{productName}), '%') OR LOWER(p.product_name) LIKE CONCAT(LOWER(#{productName}), '%') OR LOWER(p.product_name) LIKE CONCAT('%', LOWER(#{productName})) )
AND o.outgoing_status = #{productStatus}
ORDER BY o.outgoing_id;
